quest.test('Status is 200', () => {
  expect(quest.response.status).to.equal(200);
});

quest.test('Response has userId', () => {
  const body = quest.response.json();
  expect(body).to.have.property('userId');
});

quest.test('Response has title', () => {
  const body = quest.response.json();
  expect(body.title).to.be.a('string');
  expect(body.title.length).to.be.greaterThan(0);
});

quest.test('Status is 200', () => {
  expect(quest.response.status).to.equal(200);
});

quest.test('Response is array', () => {
  const body = quest.response.json();
  expect(body).to.be.an('array');
});

quest.test('Has multiple posts', () => {
  const body = quest.response.json();
  expect(body.length).to.be.greaterThan(5);
});

quest.test('Each post has required fields', () => {
  const body = quest.response.json();
  const firstPost = body[0];
  expect(firstPost).to.have.property('id');
  expect(firstPost).to.have.property('title');
  expect(firstPost).to.have.property('body');
});

