quest.test('CLI data is loaded', () => {
  const userId = quest.iteration.data.get('userId');
  console.log('userId from CLI data:', userId);
  expect(userId).to.not.be.null;
});

quest.test('Can access username from CLI data', () => {
  const username = quest.iteration.data.get('username');
  console.log('username from CLI data:', username);
  expect(username).to.not.be.null;
});

quest.test('Variables cascade from CLI data', () => {
  const email = quest.variables.get('email');
  console.log('email from cascading variables:', email);
  expect(email).to.not.be.null;
});

quest.test('CLI data count matches file', () => {
  const count = quest.iteration.count;
  console.log('Total iterations:', count);
  expect(count).to.equal(3);
});

quest.test('Can access all CLI data rows', () => {
  const allData = quest.iteration.data.all();
  console.log(`Total data rows: ${allData.length}`);
  expect(allData.length).to.equal(3);
});
