items.postRequestScript:
quest.test('This should pass', () => {
  expect(quest.response.status).to.equal(200);
});

quest.test('Another passing test', () => {
  const body = quest.response.json();
  expect(body).to.have.property('title');
});

// This should cause a SCRIPT ERROR, not a test failure
quest.test('Test before error', () => {
  expect(quest.response.status).to.equal(200);
});

// ERROR: Accessing non-existent quest property
const badValue = quest.nonExistentProperty.getValue();

// This test should never run because script errored above
quest.test('Test after error - should not execute', () => {
  expect(true).to.be.true;
});

// ERROR: Accessing non-existent property
const badValue = quest.info.requestName;

quest.test('This should not execute', () => {
  expect(true).to.be.true;
});

// ERROR: Using undefined variable
const result = someUndefinedVariable.doSomething();

quest.test('This should not execute', () => {
  expect(true).to.be.true;
});

quest.test('Syntax error example', () => {
  // ERROR: Syntax error - missing closing parenthesis
  expect(quest.response.status).to.equal(200
});
